import ViewerBase from "../Viewers/ViewerBase.js";

export default class ControllerFileBase {


    
	 /**
   * Creates an instance of ControllerFileBase.
   * @param {integer} type -Type of the file this controller hold.
   * @param {File} model - File model this controller represents.
   */
	constructor(type, fileType, model) {

		  /**
		   * Controller's type.
		 * @type {integer}
		 * @public
		 */
		this.type = type;
		
		 /**
		   * Controller's file type.
		 * @type {integer}
		 * @public
		 */
		this.fileType = fileType;
        
        
		  /**
		   * Model.
		 * @type {File}
		 * @public
		 */
		this.model = model;


		 /**
		   * Viewer.
		 * @type {object}
		 * @public
		 */
		this.viewer = null;

    }
	
	/**
   * Returns controller's type.
   * @returns {integer} 
   */
	
 	getType() {
    	return this.type;
    }

	/**
	 * Returns controller's file type.
	 * @returns {integer} 
	 */
	
 	 getFileType() {
		return this.fileType;
	}
    
    
	/**
	 * Returns name.
	 * @returns {string} 
	 */
	getName(){
		return this.model.name;
    }
    
    		
	/**
	 *  Sets name for controller's model.
	 * @param {string} value - name.
	 * @returns {void}
	 */
	setName(value) {
		this.model.setName(value);
	}
	


    		
	/**
	   * Sets viewer.
  	 * @param {ViewerBase} viewer - viewer.
  	 * @returns {void}
   */
	
	setViewer(viewer) {
		this.viewer = viewer;
	}
	/**
	 * Returns viewer.
	 * @returns {ViewerBase} 
	 */	
	getViewer() {
		return this.viewer;
    }
    
  



}

import ControllerProperties from './ControllerProperties.js';
import ControllerPlatform from './ControllerPlatform.js';
import ControllerFileBase from './ControllerFileBase.js';
import FileText from '../Models/FileText.js';
import ViewerUtilis from '../Viewers/ViewerUtilis.js';

export default class ControllerFileTXT extends ControllerFileBase{
	



	/**
   * Creates an instance of ControllerFileTXT.
   */
	constructor(name, parent_controller) {
		
		super(ViewerUtilis.CONST_FILE_TEXT, ViewerUtilis.CONST_FILE_TEXT,  new FileText(name)) 

		  /**
		   * Parent controller.
		 * @type {ControllerPlatform}
		 * @public
		 */
		this.parent_controller = parent_controller;
		
		
		  /**
		   * Controller properties.
		 * @type {ControllerProperties}
		 * @public
		 */
		this.cntr_properties = null;
		
		
 		
	}
	

		
	/**
	 *
	 *	Inits properties for this file controller.
	 * @param {object} details
	 * @returns {void} 
	 */
	initPropertiesController(details) {
		
		this.cntr_properties = new ControllerProperties(this, details, this.parent_controller.user_priv);
	}
	/**
   * Calls remove view action for the properties controller on parent.
   * @returns {void} 
   */
	removePropertiesView() {
		this.parent_controller.removeView(this.cntr_properties);
	}
	
	/**
   * Calls show action for the properties controller on parent.
   * @returns {void} 
   */
	showPropertiesView() {
		
		this.parent_controller.show(this.cntr_properties);
	}

		
	/**
   * Renames model and call rename action on parent.
   *  @param {string} value - new name.
   * @returns {void} 
   */
	
	rename(value) {
		
		
		
			
			
		
		var oldName = this.model.name;
		var newName = value;
		
		this.model.name = value;
		
		this.cntr_properties.setName(newName);
		
		this.parent_controller.renameFile(oldName, newName);
		
		
	}
			
	/**
   * Calls show action on parent.
   * @returns {void} 
   */
	show() {
		
		this.parent_controller.show(this);
	}
	


	/**
   * Returns content of the file.
   * @returns {string} 
   */
	getModelContent() {
		return this.model.content;		
	}

	/**
   * Updates model, properties and call save action on parent.
   * @returns {void} 
   */
	save() {	
	
	
			var content = this.viewer.domBody.getElementsByClassName("body-txt")[0].value;
			////console.log("content: " +content);
			
			this.model.content = content;
			
			var size_in_bits = content.length * 8;
			
			this.cntr_properties.setSize(size_in_bits);
			
			this.parent_controller.updateContent(this);
			
			
	}

	/**
   * Returns properties's controller.
   * @returns {ControllerProperties} 
   */
	getFileProperties() {
		
		return this.cntr_properties;
		
	}

	/**
	 * Inits content.
	 * @param {string} value - Content. 
	 * @returns {void} 
	 */
	initContent(value) {
		
		
		this.model.content = value;
		
	}

	/**
   * Calls delete file action on parent.
   * @returns {void} 
   */
	deleteFile() {
		
		
		this.parent_controller.deleteFile(this);
		
		
	}
		/**
   * Calls remove view action on parent.
   * @returns {void} 
   */
	removeView() {
		
		
		this.parent_controller.removeView(this);
		
	}
	
}

import ViewerPlatform from "../Viewers/ViewerPlatform.js";
import ViewerUtilis from "../Viewers/ViewerUtilis.js";
import ControllerFileTXT from "./ControllerFileTXT.js";
import FunctionsFrontend from "../Utilis/FunctionsFrontend.js";
import QueryController from "../Utilis/QueryController.js";
import Logger from "../Utilis/Logger.js";
import ControllerFileBase from "./ControllerFileBase.js";

export var jQuery = $;
export default class ControllerPlatform {
	
	
	/**
   * Creates an instance of ControllerPlatform.
   */
	constructor() {	
	
		  /**File controllers.
			 * @type {object}
			 * @public
		 */
		this.controllers = {};	
		
		/**
		 * System viewer.
		 * @type {ViewerPlatform}.
		 * @public
		 */
		this.viewer = null;
			
		/**User id.
		 * @type {integer}
		 * @public
		 */
		this.user_id = null;
		/**User name.
		 * @type {string}
		 * @public
		 */
		this.user_name = null;
		/**User privilege.
		 * @type {string}
		 * @public
		 */
		this.user_priv = null;

		/**
		   * System logger.
		 * @type {Logger}.
		 * @public
		 */
		this.logger = null;
		
		this.initUserAndPlatform();
		
		
	}

	/**
	 * Init system logger.
	 * @param {string} name - Logger name.
	 * @returns {void} 
	 */
	setLogger(name) {

		  
		this.logger = new Logger(name);

	}

	/**
	 * Init user and platform.
	 * @returns {void} 
	 */
	initUserAndPlatform() {
		
			 
		var callback_success = function(responseText) {
			
			var response = JSON.parse(responseText)[0];
			
			
			
			this.user_id = response["id"];
			this.user_name = response["name"];
			this.user_priv = response["priv"];
			
			this.setLogger(this.user_name);
			
			console.log(
				this.logger.push_raw("User '"+ this.user_name + "' logged in")
			);
			this.initPlatform()	
		
			
			
		}.bind(this);
		
		var callback_error =ViewerUtilis.showBSOD;
		
		
		QueryController.executeQuery({"ID": true}, callback_success, callback_error);
	}

	/**
	 * Setup platform viewer and modules.
	 * @returns {void} 
	 */
	initPlatform() {
		
				this.viewer = new ViewerPlatform(this);
				this.viewer.loadAvailableModules();
				
				this.fetchAllFiles();
	}
	
	
	/**
   * Fetch all files from the server and make controllers.
   * @returns {void} 
   */
	fetchAllFiles() {
		
		
			
		var callback_success = function(response) {
			
			var next_number_for_untitled_file = -1;
			
			
			
			var response = JSON.parse(response);
			
			
			console.log(
				this.logger.push(false, ""+ response.length + " files fetched")
			);
			
			
			for ( var i=0; i < response.length; ++i) {
				
				
				var node = response[i];
				var txt = node["txt"];
		
				var info = {
					name: node["name"],
					sizeInBits: node["sizeInBits"],
					sizeOnDiskInBits: node["sizeOnDiskInBits"],
					createdTimestamp: node["createdTimestamp"],
					createdByName: node["createdByName"],
					type: ViewerUtilis.CONST_FILE_TEXT
				};
		
				var cntr = this.createNewFile(info,  false);
				cntr.initContent(txt);
				this.viewer.makeIconAndShow(info["name"], cntr);
				
				
				var number = 0;
				if ( info["name"].startsWith("Untitled text") ){
					var number = parseInt( info["name"].substring("Untitled text".length+1));
					if (number > next_number_for_untitled_file)
						next_number_for_untitled_file = number;
					
				}
				

				
				
				
			}
			
			this.viewer.setUntitledCounter(next_number_for_untitled_file+1);
			
			
		}.bind(this);
		
		var callback_error =ViewerUtilis.showBSOD;
		
		
		
		QueryController.executeQuery({"allFiles": true}, callback_success, callback_error);
		
		
	}
	
	/**
   * Creates new file controller.
   * @param {object} info - Details about new file controller.
   * @param {boolean} notExistedBefore - If file not existed in database set to true.
   * @returns {ControllerFileBase} 
   */
	createNewFile(info, notExistedBefore) {
		
		var name = info["name"]
		var sizeInBits = info["sizeInBits"]
		var sizeOnDiskInBits = info["sizeOnDiskInBits"]
		var createdTimestamp = info["createdTimestamp"]
		var createdByName = info["createdByName"]
		var type = info["type"]
		
					
		if (notExistedBefore == true) {
			
			console.log(
				this.logger.push(true, name, "created")
			);
		
			
			var callback_success = this.const_callback_success.bind(this);
			var callback_error =  this.const_callback_error.bind(this);
			
			QueryController.executeQuery({"newFile":name, "createdTimestamp":createdTimestamp}, callback_success, callback_error);
			
			
			
		}
		
		switch(type) {
			

			case ViewerUtilis.CONST_FILE_TEXT:
			
				var infoExtra = {
						name: name,
						type: ViewerUtilis.CONST_FILE_TEXT,
						opens_with: ViewerUtilis.NOTEPAD_APPLICATION,
						location: "C:\/"+createdByName,
						size: sizeInBits,
						size_on_disk: sizeOnDiskInBits,
						created: createdTimestamp
					
				};
			
				 var new_file = new ControllerFileTXT(name, this);
				 new_file.initPropertiesController(infoExtra);
				 this.controllers[name] = new_file;
				 return new_file;
				
			default:
				return;
			
		}
		
	}
	
	/**
	 * Server query action: rename file controller.
	 * @param {string} oldName - Old name of the file controller.
	 * @param {string} newName - New name of the file controller.
	 * @returns {void} 
	 */
	renameFile(oldName, newName) {
		
			console.log(
				this.logger.push(true, oldName, "renamed to ["+newName+"]")
			);
		
		
			var callback_success = this.const_callback_success.bind(this);
			var callback_error =  this.const_callback_error.bind(this);
			
			
			QueryController.executeQuery({"renameFile":oldName, "newFileName": newName}, callback_success, callback_error);
			
		
	}
	/**
	 * Standard response to successful query.
	 * @param {string} response - Response from the server.
	 * @returns {void} 
	 */
	const_callback_success(response) {

			console.log(
				this.logger.push(false, response)
			);	
			
	}	
	/**
	* Standard response to unsuccessful query.
	* @param {string} response - Response from the server.
	* @returns {void} 
	*/	
	const_callback_error(response) {

			console.log(
				this.logger.push(false, response)
			);	
			this.viewer.showError(response);
	}		
	
		
	/**
	* Server query action: update file content.
	* @param {ControllerFileTXT} file_controller - file controller.
	* @returns {void} 
	*/
	
	updateContent(file_controller) {
		
		console.log(
			this.logger.push(true, file_controller.getName(), "edited")
		);
		
		var callback_success = this.const_callback_success.bind(this);
		var callback_error =  this.const_callback_error.bind(this);
			
		QueryController.executeQuery({"changeFile": file_controller.getName(), "content":file_controller.getModelContent()}, callback_success, callback_error);
		
		
	}
	

	
	
	/**
	* Server query action: delete file.
	* @param {ControllerFileTXT} file_controller - file controller.
	* @returns {void} 
	*/
	deleteFile(file_controller) {
		
		var callback_success = this.const_callback_success.bind(this);
		var callback_error =  this.const_callback_error.bind(this);
		
		
		QueryController.executeQuery({"deleteFile": file_controller.getName()}, callback_success, callback_error);
		
		
			
			
		delete this.controllers[file_controller.getName()];
			
		console.log(
			this.logger.push(true, file_controller.getName(), "deleted")
		);
		
		
	}
	


	/**
	* Frontend action: show file viewer.
	* @param {ControllerFileBase} file_controller - file controller.
	* @returns {void} 
	*/
	
	show(file_controller) {
		
		if ( file_controller.getViewer() == null ){
			this.viewer.show(file_controller);
			console.log(
				this.logger.push(true, file_controller.getName(), "opened")
			);	
			
		}
		
		
		
		
	}
	
	/**
	* Frontend action: remove file viewer.
	* @param {ControllerFileBase} file_controller - file controller.
	* @returns {void} 
	*/
	removeView(file_controller) {
		
		console.log(
			this.logger.push(true, file_controller.getName(), "closed")
		);
		
		this.viewer.removeView(file_controller);
	}
	







	/**
	* Logs off user and redirects user to the login page.
	* @returns {void} 
	*/
	logOff() {
		
		var callback_success = function() {	
			window.open("./login/login.php", "_self");
		}.bind(this);
		
		QueryController.executeQuery({"logout": true}, callback_success);
	
	}
	
	


	/**
	* Logs system error.
	* @param {string} desc - record description.
	* @returns {void} 
	*/
	logSystemError(desc) {
	
		
			console.log(
				this.logger.push_raw( "(SYSTEM)Error: " + desc + " at " + FunctionsFrontend.getTimeATM() + "." )
			);
		

	}
	
	
	
}



var WINDOWS_C = new ControllerPlatform();

import ViewerUtilis from '../Viewers/ViewerUtilis.js';
//import ViewerTXT from './Viewers/ViewerTXT.js';
import FileProperties from '../Models/FileProperties.js';
import FunctionsFrontend from '../Utilis/FunctionsFrontend.js';
import ControllerFileTXT from './ControllerFileTXT.js';
import ControllerFileBase from './ControllerFileBase.js';

export default class ControllerProperties extends ControllerFileBase{
	
	 /**
   * Creates an instance of ControllerProperties.
   * @param {ControllerFileTXT} file_controller - file controller for which properties are created..
   * @param {object} details - details for file controller.
   */
	
	constructor(file_controller, details) {
		super(ViewerUtilis.CONST_FILE_PROPERTIES, file_controller.getFileType(), new FileProperties(details));

		 /**
		 * File controller.
		 * @type {ControllerFileTXT}
		 * @public
		 */
		this.file_controller = file_controller;


 		
	}



		
	/**
   * Removes controller's viewer.
   * @returns {void}
   */	
	removeView() {
		this.file_controller.removePropertiesView();
		
	}
	


	/**
   * Returns name of controller.
   * @returns {string}
   */
	getName(){
		
		
		var type_name = ".";
		var type = this.getFileType();
		switch(type) {
			
			case ViewerUtilis.CONST_FILE_TEXT:
				type_name += "txt";
				break;
			default:
				console.log("BUG");
				return;
			
		}
		
		return this.model.name + type_name + " Properties";
	}
	

	
	/**
   *  Returns name of the file this controller properties represents.
   * @returns {string}
   */
	getFileName() {
		return this.model.getName();
	}	

	

	/**
   *  Sets type for controller's model.
   * @param {string} value - type.
   * @returns {void}
   */	
	setType(value) {
		this.model.setType(value);
	}
		
	/**
   *  Returns opensWith propertie from controller's model.
   * @returns {integer} - const identifier.
   */	
		
	getOpensWith() {
		return this.model.getOpensWith();
	}
	
	/**
   *  Sets opensWith propertie for controller's model.
   * @param {string} value - opensWith.
   * @returns {void}
   */					
	setOpensWith(value) {
		this.model.setOpensWith(value);
	}
		
	/**
   *  Returns location propertie from controller's model.
   * @returns {string}
   */	
	getLocation() {
		return this.model.getLocation();
	}
	/**
   *  Sets location propertie for controller's model.
   * @param {string} value - location.
   * @returns {void}
   */		
	setLocation(value) {
		this.model.setLocation(value);
	}
	
	/**
   *  Returns formated size propertie from controller's model.
   * @returns {string}
   */	
	getSize() {
		
		var size = this.model.getSize();
		
		
		return (size/8/1024).toFixed(1) + " KB(" + (size/8).toFixed(1) + " bytes)";
	}
	
		/**
   *  Sets size propertie for controller's model.
   * @param {BigInt} valueBits - size in bits.
   * @returns {void}
   */
	setSize(valueBits) {
		this.model.setSize(valueBits);
		this.setSizeOnDisk(valueBits+200);
	}
	
			/**
   *  Returns formated sizeOnDisk propertie from controller's model.
   * @returns {string}
   */	
	getSizeOnDisk() {
		var size = this.model.getSizeOnDisk();
		
		
		
		return (size/8/1024).toFixed(1) + " KB(" + (size/8).toFixed(1) + " bytes)";
	}	
	
		/**
   *  Sets size on disk propertie for controller's model.
   * @param {BigInt} valueBits - size in bits.
   * @returns {void}
   */
	setSizeOnDisk(valueBits) {
		this.model.setSizeOnDisk(valueBits);
	}
	
	/**
   *  Returns formated time when file is created.
   * @returns {string}
   */			
	getCreated() {
		return FunctionsFrontend.getTimeATMformatProperties(this.model.getCreated());
	}
	
		/**
   *  Sets time when file is created.
   * @param {BigInt} value - timespamt.
   * @returns {void}
   */
	setCreated(value) {
		this.setCreated(value);
	}
	
	
}

var loginForm = document.getElementById("login-form");

document.getElementsByClassName("btn_ok")[0].addEventListener("click", function () {
  loginForm.submit();
});

document.addEventListener("keydown", function(e) {
	
	if ( e.keyCode == 13 ) {
		
		
		registerPanel.style.visibility == "visible" ? registerForm.submit() : loginForm.submit();
		
	}
	
});







var loginPanel = document.getElementsByClassName("login_panel")[0];
var registerPanel = document.getElementsByClassName("register_panel")[0];


function swap_panel() {
	
	
	loginPanel.style.visibility = 
	(checkIfVisible(loginPanel) ? "hidden" : "visible");
	
	registerPanel.style.visibility =
	(checkIfVisible(registerPanel) ? "hidden" : "visible");
	
}

function checkIfVisible(dom) {
	if ( dom.style.visibility == "visible" 
		|| dom.style.visibility == "")
		return true;
	else
		return false;
}


document.getElementsByClassName("btn_options")[0].addEventListener("click", function () {
	
	swap_panel();
	
});







var registerForm = document.getElementById("register-form");

document.getElementsByClassName("register_btn_ok")[0].addEventListener("click", function () {
  registerForm.submit();
});



document.getElementsByClassName("register_btn_ok")[0].addEventListener("click", function () {
	
	swap_panel();
	
});

document.getElementsByClassName("register_btn_cancel")[0].addEventListener("click", function () {
	
	swap_panel();
	
});

export default class File {
		

	 /**
   * Creates an instance of File.
   * @param {string} name - name.
   */
	constructor(name, type) {
		/**
		 * Name of the file.
		 * @type {string} - name.
		 * @public
		 */
		this.name = name;
		
		/**
		 * Type of the file.
		 * @type {integer}
		 * @public
		 */
		this.type = type;
	}
	 /**
   * Returns name.
   * @returns {string}
   */
	getName(){
		return this.name;
	}

			/**
   * Sets name.
	* @param {string} value - name.
   * @returns {void}
   */
  setName(value) {
	this.name = value;
	}


	/**
   * Sets type.
	* @param {integer} value - type.
   * @returns {void}
   */	
  	setType(value) {
		this.type = value;
	}

	/**
	 * Returns file type.
	 * @returns {integer}
	 */
	getType(){
		return this.type;
	}
	
}

import File from './File.js';

export default class FileProperties extends File{
	
	 /**
   * Creates an instance of FileProperties.
   * @param {string} details - details.
   */
	constructor(details) {
		super(details.name, details.type);
		

		/**
		 * Opens with which application.
		 * @type {integer}
		 * @public
		 */
		this.opens_with = details.opens_with;


		/**
		 * Location on the disk of this file.
		 * @type {string}
		 * @public
		 */
		this.location = details.location


		/**
		 * Size in bits of the file.
		 * @type {BigInt}
		 * @public
		 */
		this.size = details.size;


		/**
		 * Size on disk in bits of the file.
		 * @type {BigInt}
		 * @public
		 */
		this.size_on_disk = details.size_on_disk;


		/**
		 * Timestamp when this file created.
		 * @type {BigInt}
		 * @public
		 */
		this.created = details.created;

		
		
	}

	


	
   
		
							/**
   * Returns opens with indentifier.
   * @returns {integer}
   */		
		
	getOpensWith() {
		return this.opens_with;
	}
			/**
   * Sets opens with.
	* @param {integer} value - const indentifier.
   * @returns {void}
   */					
	setOpensWith(value) {
		this.opens_with = value;
	}
		
							/**
   * Returns location.
   * @returns {string}
   */	
	getLocation() {
		return this.location;
	}	
	
			/**
   * Sets location.
	* @param {string} value - location.
   * @returns {void}
   */	
	setLocation(value) {
		this.location = value;
	}
	
							/**
   * Returns size in bits.
   * @returns {BigInt}
   */			
	getSize() {
		return this.size;
	}	
	
				/**
   * Sets size in bits.
	* @param {string} value - size in bits.
   * @returns {void}
   */	
	setSize(value) {
		this.size = value;
	}
	
							/**
   * Returns size on disk in bits.
   * @returns {BigInt}
   */		
	getSizeOnDisk() {
		return this.size_on_disk;
	}	
	
				/**
   * Sets size on disk in bits.
	* @param {string} value - size on disk in bits.
   * @returns {void}
   */	
	setSizeOnDisk(value) {
		this.size_on_disk = value;
	}
	
					/**
   * Returns created timestamp.
   * @returns {BigInt}
   */			
	getCreated() {
		return this.created;
	}	
	
					/**
   * Sets created timestamp.
	* @param {BigInt} value - timestamp.
   * @returns {void}
   */
	setCreated(value) {
		this.created = value;
	}
	
					
	
	
}

import File from './File.js';
import ViewerUtilis from '../Viewers/ViewerUtilis.js';

export default class FileText extends File{
	
	 /**
   * Creates an instance of TxtFile.
   * @param {string} name - name.
   */
