export default class FunctionsFrontend {
	

	/**
   * Returns time for the clock.
   * @returns {string}
   */
	static getTimeATMForTheClock() {
		
		var today = new Date();
			var hours =  String(today.getHours()).padStart(2, '0');
			var minutes =  String(today.getMinutes()).padStart(2, '0');
			
			return hours + ":" + minutes;
	}

	
	/**
   * Returns time for the query record.
   * @returns {string}
   */
	static getTimeATM() {
		var today = new Date();
			var dd = String(today.getDate()).padStart(2, '0');
			var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
			var yyyy = today.getFullYear();

			var minutes =  String(today.getMinutes()).padStart(2, '0');
			var hours =  String(today.getHours()).padStart(2, '0');

			var time = dd + '/' + mm + '/' + yyyy + "  " +
				+hours+":" +minutes;
				
			return time;

	}
	

	
	/**
   * Returns time for the log record.
   * @returns {string}
   */
	static getTimeATMForLogRecord() {
		
		var today = new Date();

			var seconds =  String(today.getSeconds()).padStart(2, '0');
			var minutes =  String(today.getMinutes()).padStart(2, '0');
			var hours =  String(today.getHours()).padStart(2, '0');

			var time = hours+":" +minutes+":"+seconds;
				
			return time;
			
	}
	
	/**
   * Returns time for the system log record.
   * @param {BitInt} timestamp - Record's timestamp.
   * @returns {string}
   */
	static getTimeATMForSystemLogRecord(timestamp) {
		var today = new Date();
		today.setTime(timestamp);
			var dd = String(today.getDate()).padStart(2, '0');
			var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
			var yyyy = today.getFullYear();
			
			
			var seconds =  String(today.getSeconds()).padStart(2, '0');
			var minutes =  String(today.getMinutes()).padStart(2, '0');
			var hours =  String(today.getHours()).padStart(2, '0');


			var time = dd + '/' + mm + '/' + yyyy + "  " +
				+hours+":" +minutes+":"+seconds;
				
			return time;
			
	}
		
	/**
   * Returns time for the properties created field.
   * @returns {string}
   */
	static getTimeATMformatProperties(timestamp) {
		
		var today = new Date();
		today.setTime(timestamp);
			var dd_unformatedINT = today.getDate();
			var dd = String(today.getDate()).padStart(2, '0');
			
			dd_unformatedINT = dd_unformatedINT%7;
			var dayWord = "";
			switch(dd_unformatedINT) {
				
				case 1:
					dayWord = "Monday"
					break;
				case 2:
					dayWord = "Thuesday";
					break;
				case 3:
					dayWord = "Wendesday";
					break;
				case 4:
					dayWord = "Thursday";
					break;
				case 5:
					dayWord = "Friday";
					break;
				case 6:
					dayWord = "Saturday";
					break;
				case 0:
					dayWord = "Sunday";
					break;
				
			}
			
			var mm_unformatedINT = today.getMonth() + 1;
			var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
			
			var monthWord = "";
			switch(mm_unformatedINT) {
				
				case 1:
					monthWord = "January"
					break;
				case 2:
					monthWord = "February";
					break;
				case 3:
					monthWord = "March";
					break;
				case 4:
					monthWord = "April";
					break;
				case 5:
					monthWord = "May";
					break;
				case 6:
					monthWord = "June";
					break;
				case 7:
					monthWord = "July";
					break;
				case 8:
					monthWord = "Avgust";
					break;
				case 9:
					monthWord = "September";
					break;
				case 10:
					monthWord = "October";
					break;
				case 11:
					monthWord = "November";
					break;
				case 12:
					monthWord = "December";
					break;
				
			}
			
			
			var yyyy = today.getFullYear();

			var minutes =  String(today.getMinutes()).padStart(2, '0');
			var hours =  String(today.getHours()).padStart(2, '0');
			var seconds =  String(today.getSeconds()).padStart(2, '0');

			var dayHalf = "AM";
			if (hours > 12)
				dayHalf = "PM";

			var time = dayWord + ", " + monthWord + " " + dd + ", "+ yyyy + ", " +
				+hours+":" +minutes+":"+seconds + " " + dayHalf;
				
			return time;
		
	}
	
		
}

import FunctionsFrontend from "./FunctionsFrontend.js";

export default class Logger {
	
	
	 /**
   * creates a instance of Logger.
   */
	constructor(name) {
			/**
		 * Logged actions.
		 * @type {Array} 
		 * @public
		 */
		this.logActions = [];
		this.setName(name);
	}
	
	/**
   * Sets name.
	* @param {string} name - name.
   * @returns {void}
   */	
	setName(name) {
			/**
		 * User for which actions are logged.
		 * @type {Array} 
		 * @public
		 */
		this.user_name = name;
	}

	/**
   * Push raw log record.
	* @param {string} text - record.
   * @returns {string} - formated record.
   */
	push_raw(text) {
		
		this.logActions.push(FunctionsFrontend.getTimeATMForLogRecord() + " | FRONTEND: " + text);
		return text + ".";
	}
	

		/**
   * Push log record.
	* @param {boolean} isFrontend - is front or back end record.
	* @param {string} what - file on which action happend.
	* @param {string} action - which action happend.
   * @returns {string} - formated record.
   */
	push(isFrontend, what, action) {
		var formated
		if (isFrontend)
			formated = Logger.formatFrontendRecord(what, action)
		else 
			formated = Logger.formatBackendRecord(what)
		this.logActions.push(formated);
		return formated;
	}
	
		/**
   * Format log record.
	* @param {string} text - log record.
	 * @returns {string} - formated record.
   */
	static 
	formatBackendRecord(text) {
		
		
		if ( text.startsWith("Error") ) 
			text = text.substring("Error: ".length);
		else if ( text.startsWith("Successful") )
			text = text.substring("Successful: ".length);
			
		
		return FunctionsFrontend.getTimeATMForLogRecord() + " | BACKEND: " + text + ".";
		
	}
	/**
   * Format log record for frontend.
	* @param {string} file - file on which action happend.
	* @param {string} action - which action happend.
   * @returns {string} - formated record.
   */
	static 
	formatFrontendRecord(file, action) {
		
		
		return FunctionsFrontend.getTimeATMForLogRecord() + " | FRONTEND: File ["+file+"] is " + action + ".";
		
		
	}
			/**
   * Returns array of log records.
  * @returns {Array} - generated array of log records.
   */
	generate_array_of_strings() {
		
		
							
		var array_result = [];
		
		array_result.push(FunctionsFrontend.getTimeATM());
		array_result.push("");
		array_result.push("");
		array_result.push("Windows XP report for '" + this.user_name + "'");
		array_result.push("");
		
		
		array_result.push("Actions: ");
		
		for (var i = 0; i < this.logActions.length; ++i) {
			
				var record = this.logActions[i];
				if (record.length > 65) {
					for (var j = 0; j < record.length; j+=65) {
							var start = j;
							var end = Math.min(j+65, record.length);
							array_result.push(record.substring(j, end));
							
						}
						
				} else {
					array_result.push(record);
				}
		}
		
		return array_result;
		
	}
	
	
}

export default class QueryController {
			
	/**
   * Query server.
	* @param {string} POST_params - params for post action.
	* @param {string} callback_success - callback for successful request.
	* @param {string} callback_error - callback for unsuccessful request.
  * @returns {void}
   */
	static 
	executeQuery(POST_params, callback_success, callback_error) {
		
			var request = QueryController.ajaxRequest();
				request.open("POST", "./php/dataProvider.php", true);
				request.setRequestHeader("Content-type", "application/x-www-form-urlencoded");
				request.onreadystatechange = function() {
					if(this.readyState == 4)
					{
						if (this.status == 200) {
							
								if(this.responseText != null)
								{
									
									if ( callback_success!=undefined)
										callback_success(this.responseText);
									
								}
							
						} else {
							
									if ( callback_error!=undefined)
										callback_error(this.responseText);
						}
						
					}
					
				};
				
			
			
			var keys = Object.keys(POST_params);
			
			var POST_string = "";
			for ( var i = 0; i<keys.length; ++i) {
				
				var key =  keys[i];
				var value = POST_params[key];
				POST_string += key + "=" + value;
				POST_string += "&";
				
			}
			if ( POST_string.length > 1 ) 
				POST_string = POST_string.substring(0, POST_string.length-1);
			
			
			
			request.send(POST_string);
	}

	/**
   	* Returns ajax object.
	* @returns {XMLHttpRequest}
   */
	static 
	ajaxRequest() {

		try {
			var request = new XMLHttpRequest(); // novi browseri
		}
		catch(e1) {
			try {
				request = new ActiveXObject("Maxm12.XMLHTTP"); // IE6
			}
			catch(e2) {
				try {
					request = new ActiveXObject("Microsoft.XMLHTTP"); // IE...
				}
				catch(e3) {
					request = false;
				}	
			}				
		}
		return request;
	}
	

	
}

