import ViewerUtilis from './ViewerUtilis.js';
import WindowBuilder from './Builders/WindowBuilder.js';
import ControllerFileBase from '../Controllers/ControllerFileBase.js';

export default class ViewerBase {
	
	
	/**
	 *Creates an instance of ViewerBase.
	 * @param {WindowBuilder} builder
	 * @param {ControllerFileBase} file_controller
	 * @param {string} user_priv
	 * @param {object} modules
	 * @param {integer} posX
	 * @param {integer} posY
	 * @param {integer} sizeX
	 * @param {integer} sizeY
	 */
	constructor(builder, file_controller, user_priv, modules, posX, posY, sizeX, sizeY) {
		/**
		 * File controller.
		 * @type {ControllerFileBase} 
		 * @public
		 */
		this.file_controller = file_controller;
		/**
		 * Type identificator.
		 * @type {integer} 
		 * @public
		 */
		this.type = this.file_controller.getType();

		/**
		 * Left upper x coord.
		 * @type {integer} 
		 * @public
		 */
		this.posX = posX;
		/**
		 * Left upper y coord.
		 * @type {integer} 
		 * @public
		 */
		this.posY = posY;

		/**
		 * Window width.
		 * @type {integer} 
		 * @public
		 */
		this.sizeX = sizeX;

		/**
		 * Window height.
		 * @type {integer} 
		 * @public
		 */
		this.sizeY = sizeY;
		
		var taskbarModule = modules["taskbarModule"];
		var result = builder.build(file_controller, user_priv, taskbarModule);
		
		/**
		 * Window dom.
		 * @type {integer} 
		 * @public
		 */
		this.dom = result.dom;
		/**
		 * Window body dom.
		 * @type {integer} 
		 * @public
		 */
		this.domBody = result.windowBody;
		
		this.dom.style.left = posX+"px";
		this.dom.style.top = posY+"px";
		this.dom.style.width = sizeX + "px";
		this.dom.style.height = sizeY + "px";
		
		
		
	}
	
	/**
	 *
	 * Returns DOM.
	 * @returns {DOM}
	 */
	getDom() {
		return this.dom;
	}
	
	
}

import ViewerUtilis from './ViewerUtilis.js';
import ControllerFileTXT from '../Controllers/ControllerFileTXT.js';
import RenamingModule from './widgets/RenamingModule.js';
import Highlight from './widgets/Highlight.js';
import TaskbarModule from './widgets/TaskbarModule.js';


export default class ViewerIcon {
	
	
	/**
	 *Creates an instance of ViewerIcon.
	 * @param {ViewerPlatform} viewer_platform
	 * @param {ControllerFileTXT} txt_controller
	 * @param {integer} position_x
	 * @param {integer} position_y
	 */
	constructor(viewer_platform, txt_controller, position_x, position_y) {
			/**
		 * Viewer platform.
		 * @type {ViewerPlatform} 
		 * @public
		 */
		this.viewer_as_arg = viewer_platform;
			/**
		 * Textual controller.
		 * @type {ControllerFileTXT} 
		 * @public
		 */
		this.txt_controller = txt_controller;
			/**
		 * Friend module: renaming module.
		 * @type {RenamingModule} 
		 * @public
		 */
		this.renamingModule = this.viewer_as_arg.getModule("renamingModule");
		/**
		 * Friend module: highlight module.
		 * @type {Highlight} 
		 * @public
		 */
		this.highlightModule = this.viewer_as_arg.getModule("highlightModule");
		/**
		 * Friend module: taskbar module.
		 * @type {TaskbarModule} 
		 * @public
		 */
		this.taskbarModule = this.viewer_as_arg.getModule("taskbarModule");
		
		/**
		 * Dom of this viewer.
		 * @type {DOM} 
		 * @public
		 */
		this.dom = this.make_new_icon(txt_controller, position_x, position_y);
		

		/**
		 * Timeout object for controlling icon double clicking.
		 * @type {object} 
		 * @public
		 */
		this.timeoutClick = {
			clickedCounter: 0,
			callback: null
		};
		
		this.timeoutClick.callback = function() {
				
				
				if ( this.timeoutClick.clickedCounter == 2 ) {
					this.renamingModule.stopRenaming();				
					this.txt_controller.show();
				} 
					
				this.timeoutClick.clickedCounter = 0;
				
		}.bind(this);
		
	}


	/**
   * Returns dom that represent title of the icon.
   * @returns {DOM}
   */
	getIconTitleDOM() {
		return this.dom.getElementsByClassName("txt-icon-title")[0];
	}
	
	
	/**
	 * Returns dom for the renaming field.
	 * @returns {DOM}
	 */
	getRenameFieldDOM() {
		return this.dom.getElementsByClassName("txt-icon-title-rename")[0];
	}
	

	
	/**
	 * Returns textual controller.
	 * @returns {ControllerFileTXT}
	 */	
	getController() {
		return this.txt_controller;
	}

	

	/**
	 * Returns main viewer DOM.
	 * @returns {DOM}
	 */	
	getDom() {
		return this.dom;
	}
	

	/**
	 * Returns name of the icon.
	 * @returns {string}
	 */	
	getName() {
		return this.txt_controller.getName();
	}
	
	
	
	/**
	 * Makes new icon at position x and y.
	* @param {ControllerFileTXT} txt_controller - Textual controller.
	* @param {integer} x - position x axis.
	* @param {integer} y - position y axis.
	 * @returns {DOM}
	 */	
	make_new_icon(txt_controller, x, y) {
	
		
		var icon_file = document.createElement("div");
		icon_file.className = "txt-icon";
		icon_file.style.left = x + "px";
		icon_file.style.top = y + "px";
		
		
		
		var icon = document.createElement("img");
		icon.className = "txt-icon-image";
		if (txt_controller.getName() == "config"){
			icon.src = ViewerUtilis.ICON_CONFIG_FILE;
			icon_file.className += " config-file"
		}
		else
			icon.src = ViewerUtilis.ICON_TXT_FILE;
		
		
		
		
		var icon_txt = document.createElement("h4");
		icon_txt.className = "txt-icon-title";
		icon_txt.innerHTML = txt_controller.getName();
	
		
		
		
		var icon_txt_rename = document.createElement("input");
		icon_txt_rename.className = "txt-icon-title-rename";
		icon_txt_rename.style.visibility = "hidden";
		icon_txt_rename.value = txt_controller.getName();
		
		
		icon_txt_rename.addEventListener("click", function(e) {
			
			e.stopPropagation();
			
		}.bind(this),false);
		
		icon_txt_rename.addEventListener("keyup", function(e) {
			
			
			
			
			  if (e.keyCode === 13) {
				  
				var old_name = this.txt_controller.getName();
				 
				var renameField = this.getRenameFieldDOM();
				var new_name = renameField.value;
				 //console.log("RENAME 13");
				 //console.log(renameField);
				this.rename(old_name, new_name);
	 
				  
				e.preventDefault();
				e.stopPropagation();
			  }
			
		}.bind(this), false);
		
		
		icon_file.appendChild(icon);
		icon_file.appendChild(icon_txt);
		icon_file.appendChild(icon_txt_rename);
		
		icon_file.addEventListener("click", function(e) {
			
			var viewer_as_arg = this.viewer_as_arg;
			
			this.viewer_as_arg.resetModules();
			this.highlightModule.highlightIcon(icon_file, true);
			
			if ( this.timeoutClick.clickedCounter == 0 ) {
				this.timeoutClick.clickedCounter+=1;
				setTimeout(this.timeoutClick.callback,200);			
			} else {
				if(this.timeoutClick.clickedCounter < 2){
					this.timeoutClick.clickedCounter += 1;
				}
			}
			
		
			
			
			
			
			e.stopPropagation();
			
		}.bind(this),false);
		
		icon_file.addEventListener("contextmenu", function(e) {
			
			var viewer_as_arg = this.viewer_as_arg;
			
			this.viewer_as_arg.resetModules();
			//this.highlightModule.reset();
			//this.highlightModule.highlightIcon(icon_file, true);
			
			var module = viewer_as_arg.getModule("rightMouseClickModule");
			
			if ( !module ){
			
				console.log("no module");
				return;
			}
			
			if ( module.checkDependency() == false ){
				console.log("checkDependency false");
				return;
			}
			
			module.hide_hud_panel();
			module.resetOtherModules();
			module.show_icon_panel(e.clientX+"px", e.clientY+"px");
			
			//viewer_as_arg.setSelectedIcon({ controller: txt_controller, icon: icon_file});
			viewer_as_arg.setSelectedIcon(this);
			
			
			e.preventDefault();
			e.stopPropagation();
			
		}.bind(this), false);
		
		
		var viewer_as_arg = this.viewer_as_arg;
		var highlightModule = this.highlightModule;
		
		jQuery(function() {
			 jQuery( icon_file ).draggable({
				start: function(event,ui) {
					
					viewer_as_arg.resetModules();
					highlightModule.highlightIcon(icon_file, true);
					
				},
				stop: function(event, ui) {
					
					//viewer_as_arg.icon_file_click_twice = false;
				
				}
			});
		});
		
		return icon_file;
	}
	

	
	
	/**
	 * Renaming stop action.
	 * @returns {void}
	 */	
	stopRenaming() {
		
		var old_name = this.txt_controller.getName();
		 
		var renameField = this.getRenameFieldDOM();
		var new_name = renameField.value;
		this.rename(old_name, new_name);
			
	}
	

		
	/**
	 * Renames icon with new name.
	* @param {string} old_name - old name.
	* @param {string} new_name - new name.
	 * @returns {void}
	 */	
	rename(old_name, new_name) {
		old_name =  old_name.trim();
		new_name =  new_name.trim();
		
		
		 if (old_name.localeCompare(new_name) != 0 
					 && !this.viewer_as_arg.iconExists(new_name)) {	
					 
				this.txt_controller.rename(new_name);
				this.viewer_as_arg.renameIcon(old_name, new_name);
					 
		 } else {
				this.getRenameFieldDOM().value = old_name;
		 }
	
		this.renamingModule.setActive(false, null);
		this.switchTitleAndRenamingField()
		 
							 
	}
	
	
	

	/**
	 * Toggles title field and renaming field.
	 * @returns {void}
	 */	
	switchTitleAndRenamingField(){
		
		var icon_title_dom = this.getIconTitleDOM();
		var icon_rename_field_dom = this.getRenameFieldDOM();
		
		icon_rename_field_dom.innerHTML = this.txt_controller.getName();
		icon_title_dom.innerHTML = this.txt_controller.getName();
		
		if (icon_title_dom.style.visibility == "visible" || icon_title_dom.style.visibility == "") {
		
			icon_title_dom.style.visibility = "hidden";
			icon_rename_field_dom.style.visibility = "visible";
			
		} else {	  
		
			icon_rename_field_dom.style.visibility = "hidden";
			icon_title_dom.style.visibility = "visible";
			
		}
		
	}
	
}

import ViewerUtilis from './ViewerUtilis.js';
import ControllerPlatform, {jQuery} from "../Controllers/ControllerPlatform.js";
import ViewerTXT from "./ViewerTXT.js";
import PanelCreator from "../Viewers/widgets/PanelCreator.js";
import Highlight from "../Viewers/widgets/Highlight.js";
import RenamingModule from "../Viewers/widgets/RenamingModule.js";
import TaskbarModule from "../Viewers/widgets/TaskbarModule.js";
import ViewerIcon from "./ViewerIcon.js";
import ViewerProperties from "./ViewerProperties.js";
export default class ViewerPlatform {


	/**
	 *
	 *Resets modules on which module that called this function doesn't depends.
	 * @param {object} modulesToReset - modules to reset.
	 * @returns {void}
	 */
	resetConcreteModules(modulesToReset) {
		
		
		for (var i = 0; i <modulesToReset.length; ++i ) {
			
			var moduleName = modulesToReset[i];
			var module = this.features[moduleName];
			if (module)
				module.reset();
		}
		
		
		
	}
	

	/**
	 *
	 *Checks activness of modules on which module that called this function depends.
	 * @param {object} modulesToCheck - modules to check.
	 * @returns {boolean}
	 */

	checkDependency(modulesToCheck) {
		
		for ( var i=0; i < modulesToCheck.length; ++i) {
			
			var moduleName = modulesToCheck[i];
			var module = this.features[moduleName];
			if ( module.isActive() )
				return false;
				
			
			
		}
		
		return true;
		
	}
	

	
	/**
	 *
	 * Init taskbar module unit.
	 * @returns {void}
	 */
	
	initTaskBarModule() {
		
		
		var params = {
			
			taskbarModule: {
				viewer_as_arg: this,
				actionDependOn: [],
				actionResetModules: ["highlightModule", "renamingModule", "rightMouseClickModule"],
				
				callbacks: {
					appendChildCustom: this.appendChildCustom.bind(this),
					getUserName: this.getUserName.bind(this),
					logOff: this.logOff.bind(this),
					logSystemError: this.controller.logSystemError.bind(this),
					getLogger: this.getLogger.bind(this)
				}
			} 
			
		};
		
		this.features["taskbarModule"] = new TaskbarModule(params);
	}
		
	
	/**
	 *
	 * Init renaming module unit.
	 * @returns {void}
	 */
	initRenamingModule() {
		
		
		var params = {
			
			renamingModule: {
				viewer_as_arg: this,
				actionDependOn: [],
				actionResetModules: [],
				
				callbacks: {
					logSystemError: this.controller.logSystemError.bind(this)
				}
			} 
			
		};
		
		this.features["renamingModule"] = new RenamingModule(params);
	}
		
	/**
	 *
	 * Init right click module unit.
	 * @returns {void}
	 */
	initRightClickPanelsFeature(){
		
		
		var params = {
			
			rightClickPanel: {
				viewer_as_arg: this,
				actionDependOn: [],
				actionResetModules: ["highlightModule", "renamingModule"],
				friendModules: {
					renamingModule: this.features["renamingModule"]
				},
				callbacks: {
					appendChildCustom: this.appendChildCustom.bind(this),
					getSelectedIcon: this.getSelectedIcon.bind(this),
					setSelectedIcon: this.setSelectedIcon.bind(this),
					deleteSelectedIcon: this.deleteSelectedIcon.bind(this),
					getIconsDictionary: this.getIconsDictionary.bind(this),
					resetPositionIconPointer: this.resetPositionIconPointer.bind(this),
					getPositionIconPointer: this.getPositionIconPointer.bind(this),
					movePositionIconPointer: this.movePositionIconPointer.bind(this),
					makeIconAndShow: this.makeIconAndShow.bind(this),
					logSystemError: this.controller.logSystemError.bind(this)	
				},
				userPriv: this.controller.user_priv
			} 
			
		};
		
		this.features["rightMouseClickModule"] = new PanelCreator(params);
	}	
		
	
	/**
	 *
	 * Init highlight module unit.
	 * @returns {void}
	 */
	initHighlightFeature(){
		
		
		var dependency = ["rightMouseClickModule", "renamingModule"];
		var resetOnAction = [];
		
		var params = {
			
			highlightModule: {
				viewer_as_arg: this,
				actionDependOn: dependency,
				actionResetModules: resetOnAction,
				
				callbacks: {
					getIconsDictionary: this.getIconsDictionary.bind(this)
				}
			}
			
		};
		
		this.features["highlightModule"] = new Highlight(params);
	}
	

	/**
	 *
	 * Returns selected icon viewer.
	 * @returns {ViewerIcon}
	 */
	getSelectedIcon() {
		return  this.properties.selectedIconProperty;
	}
	


	/**
	 *
	 * Sets selected icon viewer.
	 * @param {ViewerIcon} icon - Viewer icon.
	 * @returns {void}
	 */
	setSelectedIcon(icon) {
		this.properties.selectedIconProperty = icon;
	}
	
	/**
	 *
	 * Deletes selected icon viewer.
	 * @param {ViewerIcon} iconViewer - Viewer icon.
	 * @returns {void}
	 */
	deleteSelectedIcon(iconViewer){

		if ( iconViewer != this.properties.selectedIconProperty )
			console.log("NE PODUDARAJU SE");
		else
			console.log(" PODUDARAJU SE");

		this.dom.removeChild(iconViewer.getDom());
		delete this.icons[iconViewer.getName()];
				
	}
	
	/**
	 *
	 * Renames selected icon viewer.
	 * @param {string} old_name - old name.
	 * @param {string} new_name - new name.
	 * @returns {void}
	 */
	renameIcon(old_name, new_name) {
		
		this.icons[new_name] = this.icons[old_name];
		delete this.icons[old_name];
	}
	/**
	 *
	 * Returns module.
	 * @param {string} name - Module name.
	 * @returns {ModuleBase}
	 */
	getModule(name) {
		return this.features[name];
		
	}
	

	
	/**
	 * creates a instance of ViewerPlatform
	 * @param {ControllerPlatform} controller - platform controller.
	 */
	constructor(controller) {
		
			/**
		 * Platform controller.
		 * @type {ControllerPlatform} 
		 * @public
		 */
		this.controller = controller;
			/**
		 * Total number of files.
		 * @type {ControllerPlatform} 
		 * @private
		 */
		this.totalFileNumber = 0;

		
		/**
		 * Icons.
		 * @type {ControllerPlatform} 
		 * @private
		 */
		this.icons = {};
		
		
		/**
		 * Properties.
		 * @type {object} 
		 * @private
		 */
		this.properties = {untitledCounterProperty: -1,
							selectedIconProperty: null};

		/**
		 * Features - modules.
		 * @type {object} 
		 * @public
		 */	
		this.features = {
			highlightModule: null,
			rightMouseClickModule: null,
			renamingModule: null,
			taskbarModule: null
						
		};

		/**
		 * Position pointer.
		 * @type {object} 
		 * @public
		 */
		this.iconPositionPointer = {
			
			x: 10,
			y: 10
			
		};
		
		
		
		
		this.initEventHudClicked()
		
		
		
		this.dom = this.createPlatformView();
		
		this.z_index = 5;
		
		
		
		
		document.body.appendChild(this.dom);
		
		
		
	}
	/**
	 *
	 * Loads available modules.
	 * @returns {void}
	 */
	loadAvailableModules() {

		this.initRenamingModule();		
		this.initRightClickPanelsFeature();
		this.initHighlightFeature();
		this.initTaskBarModule();
	
	}
	/**
	 *
	 * Returns next z index.
	 * @returns {void}
	 */
	getNextZindex(){
		this.z_index+=1;
		return this.z_index;
	}
	/**
	 *
	 * Creates platform view.
	 * @returns {void}
	 */
	createPlatformView() {
		var HUD = document.createElement("div");
		HUD.className = "hud";
	
		
		return HUD;
	}

	
	/**
	 *
	 * Resets all modules.
	 * @returns {void}
	 */
	resetModules() {
		var keys = Object.keys(this.features);
			
			for ( var i = 0; i <keys.length; ++i) {
			
				var key = keys[i];
				var module = this.features[key];
				module.reset();
			
			}
		
			
			
	}

	/**
	 *
	 * Init hud click.
	 * @returns {void}
	 */
	initEventHudClicked() {
		document.addEventListener("click", function(e){
				
			this.resetModules();
			
			this.closeMenu();
			//var icon_viewer = this.slectedIcon.icon;
			//icon_viewer.stopRenaming();
			
			
		
			e.preventDefault();
		}.bind(this), false);
	}

	/**
	 *
	 * Sets next untitled number.
	 * @param {integer} value - next untitled number.
	 * @returns {void}
	 */
	setUntitledCounter(value) {
		this.properties["untitled_counter"] = value;	
	}	
	
	/**
	 *
	 * Returns logger.
	 * @returns {Logger}
	 */
	getLogger() {
		
		return this.controller.logger;
		
	}

	/**
	 *
	 * Icons pagination.
	 * @returns {void}
	 */
	icons_pagination() {
		
		if ( this.totalFileNumber > 30 ) {
			
			console.log("PAGINATION");
			var icon_array = document.getElementsByClassName("txt-icon");
							
			for(var i = 0;i < icon_array.length; ++i) {
				
				icon_array[i].className += " zoomed";
				
			}
		}
		
		
	}
	
	
	/**
	 *
	 * Shows file controller viewer. *
	 *  @param {ControllerFileBase} file_controller - controller which's viewer need to be showed.
	 * @returns {void}
	 */
	show(file_controller) {
		
		if ( file_controller.getViewer() != null )
			return;
		
		
		//console.log(txt_controller);
									//txt_controller, this-me-as-viewer-parent, positions....
		
		var file_type = file_controller.getType();
		var file_viewer = null;
		
		var position_x = 50 + Math.random()*90
		var position_y = 50 + Math.random()*90
		
		var modules = {
			
			taskbarModule: this.features["taskbarModule"]
			
		};
		
		switch(file_type) {
			
			case ViewerUtilis.CONST_FILE_TEXT:
				file_viewer = new ViewerTXT(file_controller, this.controller.user_priv, modules,  position_x, position_y, ViewerUtilis.CONST_FILE_SIZE_X, ViewerUtilis.CONST_FILE_SIZE_Y);
				break;
			case ViewerUtilis.CONST_FILE_PROPERTIES:
				file_viewer = new ViewerProperties(file_controller, this.controller.user_priv, modules, position_x, position_y, ViewerUtilis.CONST_PROPERTIES_SIZE_X, ViewerUtilis.CONST_PROPERTIES_SIZE_Y);
				break;	
			default:
				this.controller.logSystemError("File type: " + file_type + " not supported.");
				return;
			
		}
		
		this.features["taskbarModule"].addTask(file_controller.getName());
		
		file_controller.setViewer(file_viewer);
		this.dom.appendChild(file_controller.getViewer().getDom());
		
	}

	/**
	 *
	 * Removes file controller viewer.
	 * @param {ControllerFileBase} file_controller - controller which's viewer need to be destroyed.
	 * @returns {void}
	 */
	removeView(file_controller) {
		
		//console.log(txt_controller);
		
		
		this.features["taskbarModule"].removeTask(file_controller.getName());
		
		this.dom.removeChild(file_controller.getViewer().getDom());
		
		
		delete file_controller.viewer;
		
	}

	/**
	 *
	 * Closes start menu.
	 * @returns {void}
	 */
	closeMenu() {
			var menu = document.getElementsByClassName("main-menu")[0];
			
			menu.style.visibility = "hidden";
	}
	
	
	

	
	/**
	 *
	 * Makes icon.
	 * @param {string} fileName - name of the file.
	 * @param {string} fileController - controller of the file.
	 * @param {string} isFromPanel - is request made from right click module.
	 * @returns {void}
	 */
	makeIconAndShow(fileName, fileController, isFromPanel) {

			var x;
			var y;
			var name = "";
			var new_file = null;
			
			var position = this.iconPositionPointer;
				
			x = position.x;
			y = position.y;


			if ( fileName == undefined ){


				if ( isFromPanel ) {
				
					var module = this.getModule("rightMouseClickModule");
	
						var hud_panel_position = module.get_hud_panel_position_px();
						x = hud_panel_position["x"];
						y = hud_panel_position["y"];
						
						
						x = parseInt(x.substring(0, x.search("p")))- 30;
						y = parseInt(y.substring(0, y.search("p"))) - 40;
						
	
	
					
					
				}

				
				name = "Untitled text " + this.properties["untitled_counter"];
				this.properties["untitled_counter"] += 1;


				var info = {
					name:name,
					type: ViewerUtilis.CONST_FILE_TEXT,
					sizeInBits: 0,
					sizeOnDiskInBits: 0,
					createdByName: this.controller.user_name,
					createdTimestamp: new Date().getTime()
				
				};

				new_file = this.controller.createNewFile(info,  true);	
				
			} else {
				this.movePositionIconPointer();
				
				name = fileName;
				new_file = fileController;
			}
			
		
			
				
			
			
			
			var icon_viewer = new ViewerIcon(this, new_file, x, y);
			this.icons[name] = icon_viewer;
			this.dom.appendChild(icon_viewer.dom);
		
		
			
			
			
			
		
			this.totalFileNumber += 1;
			this.icons_pagination();
				 
		
	}
	
	/**
	 *
	 * Moves pointer for the next icon.
	 * @returns {void}
	 */
	movePositionIconPointer() {
			
		// move pointer
		var screen_height = 480;
		var icon_width = 100;
		var icon_height = 90;
		
		var offset_x = 0;
		var offset_y = 0;
	
		this.iconPositionPointer.y += offset_y + icon_height;
			
			
	
		if (this.iconPositionPointer.y > screen_height-icon_height) {
			
			this.iconPositionPointer.y = 10;
			this.iconPositionPointer.x += offset_x + icon_width;
			
			
		}
	}
	
	/**
	 *
	 * Resets pointer for the next icon.
	 * @returns {void}
	 */
	resetPositionIconPointer() {
		
		this.iconPositionPointer.x = 10;
		this.iconPositionPointer.y = 10;
		
	}
	

	
	/**
	 *
	 * Gets pointer for the next icon.
	 * @returns {object}
	 */
	getPositionIconPointer() {
		return this.iconPositionPointer;
	}
	


	/**
	 *
	 * Gets icons dictionary.
	 * @returns {object}
	 */
	getIconsDictionary() {
		return this.icons;
	}
	
	/*
	
		name: iconExists
		description:
			Cheks if icon with name exist.
		return:
			true: if icon exists
			false: !true
	
	*/

	
	/**
	 *
	 * Checks if icon exists.
	 * @param {string} name - name of the icon.
	 * @returns {boolean}
	 */
	iconExists(name) {
		
		return (name in this.icons);
		
	}
	
	/*
	
		name:appendChildCustom
		description:
			appends function's argument to the dom
		returns:
			void
	*/

	/**
	 *
	 * Appends to the viewer main dom.
	 * @param {DOM} child - Child's dom.
	 * @returns {void}
	 */
	appendChildCustom(child) {
		
		this.dom.appendChild(child);
		
	}

	/**
	 *
	 * Returns name of the user currently logged in.
	 * @returns {string}
	 */
	getUserName() {
		
		return this.controller.user_name;
		
	}
	/**
	 *
	 * Logs off user.
	 * @returns {void}
	 */
	
	logOff() {
		this.controller.logOff();
	}
	
}

