		/**
		 * Dom of right click hud panel.
		 * @type {DOM} 
		 * @public
		 */
		this.right_click_hud_panel_dom = null;
		
		/**
		 * Indicator if right click icon panel is visible(active).
		 * @type {boolean} 
		 * @public
		 */
		this.is_right_click_icon_active = false;
